import PropTypes from 'prop-types'
import Image from 'next/image'

const Photo = ({ fileName, ...props }) => {
  const blurURL =
    'data:image/webp;base64,'
  return (
    <Image
      src={`/media/${fileName}`}
      alt={fileName}
      fill={props.fill ?? false}
      placeholder="blur"
      blurDataURL={blurURL}
      {...props}
    />
  )
}

Photo.propTypes = {
  fileName: PropTypes.string,
}

export default Photo
